import React from 'react';
import {Form, Button} from "react-bootstrap";
import LineGraph from "./graphComponents/LineGraph";

const newblock = {
    backgroundColor: "whitesmoke",
    borderRadius: "50px",
    display: "block",
    width: "750px",
    height: "600px",
    marginLeft: "50px",
    marginTop: "30px",
    alignContent: "center"
}


export default class GraphForm extends React.Component {
    state = {
        fileName: "",
        predFrequency: "",
        numPred: 0,
        graphType: ""
    }

    change= (e) => {
        this.setState({
            [e.target.name]: e.target.value
        });
    };

    onSubmit = e => {
        e.preventDefault();
        if(this.state.graphType === "Line"){
            return <LineGraph>
                        file = {this.state.fileName}
                        numPredictions = {this.state.numPred}
                        frequency = {this.state.predFrequency}
                        chartData = graphData
                   </LineGraph>
        }
        else if(this.state.graphType === "Bar"){

        }
        else if(this.state.graphType === "Pie"){

        }
        else if(this.state.graphType === "Scatter"){

        }

    };

    graphData = {
        labels: ['Boston', 'Worcester', 'Springfield', 'Lowell', 'Cambridge', 'New Bedford'],
        datasets:[
            {
                label:'Population',
                data:[
                    617594,
                    181045,
                    153060,
                    106519,
                    105162,
                    95072
                ],
                backgroundColor:[
                    'rgba(255, 99, 132, 0.6)',
                    'rgba(54, 162, 235, 0.6)',
                    'rgba(255, 206, 86, 0.6)',
                    'rgba(75, 192, 192, 0.6)',
                    'rgba(153, 102, 255, 0.6)',
                    'rgba(255, 159, 64, 0.6)',
                    'rgba(255, 99, 132, 0.6)'
                ]
            }
        ]
    }

    render() {
        return(
            <div style={newblock}>
                <br/>
                <div style={{marginLeft: "20px", marginTop: "20px", alignContent: 'center', fontSize:'1.8em', fontWeight:'bold'}}> Generate a New Report</div>
                <br></br>
                <div style={{textAlign: 'left', alignContent:'center'}}>
                    <Form style={{marginLeft: '20px', marginRight: '20px'}}>
                        <label> Report Name: </label>
                        <br/>
                        <input
                            style={{width: "650px", height: "35px"}}
                            name="fileName"
                            placeholder="My New Report"
                            value = {this.state.fileName}
                            onChange = {e => this.change(e)}
                        />
                        <br/><br/>

                        <label> Graph Type: </label>
                        <br/>
                        <select
                            name = "graphType"
                            style={{width: "300px", height: "35px"}}
                            value = {this.state.graphType}
                            onChange={e => this.change(e)}
                        >
                            <option value = "Graph Type"> My Graph</option>
                            <option value = "Line">Line</option>
                            <option value = "Bar">Bar</option>
                            <option value = "Scatter">Scatter</option>
                            <option value = "Pie">Pie</option>
                        </select>
                        <br/><br/>

                        <label> Prediction Frequency: </label>
                        <br/>
                        <select
                            name = "predFrequency"
                            style={{width: "300px", height: "35px"}}
                            value = {this.state.predFrequency}
                            onChange={e => this.change(e)}
                        >
                            <option value = "Prediction Frequency">Frequency</option>
                            <option value = "Daily">Daily</option>
                            <option value = "Weekly">Weekly</option>
                            <option value = "Monthly">Monthly</option>
                        </select>

                        <br/><br/>
                        <label> Number of Predictions: </label>
                        <br/>
                        <input
                            style={{width: "80px", height: "35px"}}
                            name="numPred"
                            placeholder=" "
                            value = {this.state.numPred}
                            onChange = {e => this.change(e)}
                        />

                        <br/><br/>
                        <Button
                            variant="outline-dark"
                            type="submit"
                            style={{textAlign: 'center', marginLeft: '500px', marginTop: "30px", width: "150px", height: "40px"}}
                            onClick = {(e) => this.onSubmit(e)}
                        >
                            Submit
                        </Button>

                    </Form>
                </div>
            </div>
        );
    }
}
