import React, {useState} from 'react';
import {ToggleButton, ToggleButtonGroup} from "react-bootstrap";


const newblock = {
    backgroundColor: "whitesmoke",
    borderRadius: "50px",
    display: "block",
    width: "1200px",
    height: "600px",
    marginLeft: "220px",
    marginTop: "40px",
}

function ToggleButtonGroupControlled() {
    const [value, setValue] = useState([1, 3]);
    const handleChange = (val) => setValue(val);
    return (
        <ToggleButtonGroup type="checkbox" style={{marginLeft: '25px'}} value={value} onChange={handleChange}>
            <ToggleButton variant="dark" value={1}>Yes</ToggleButton>
            <ToggleButton variant="dark" value={2}>No</ToggleButton>
        </ToggleButtonGroup>
    );
}

export const Settings = (props) => (
    <>
        <div style={newblock}>
            <div style={{alignContent: 'center', marginLeft: '390px', marginTop: '20px', fontSize:'1.8em', fontWeight:'bold'}}> User Settings </div>
            <div style={{alignContent: 'left', marginLeft: '35px', marginTop: '20px', fontSize:'1.2em'}}> Modify your user settings here: </div>
            <div style={{alignText: 'center', alignContent:'center', paddingTop:'15px', fontSize: '1.2em'}}>
                Receive Weekly Reports
                <ToggleButtonGroupControlled />
            </div>
        </div>
    </>
)
