/*
    Component to retrieve the Sales from the QuickBooks API
    Prints the most recent 25 sales from the sandbox
    @Notes: Lyka Marcelino, 04/04/2020
 */

import React from 'react';
import {Form, Button} from "react-bootstrap";

const newblock = {
    backgroundColor: "yellow",
    borderRadius: "50px",
    display: "block",
    width: "700px",
    height: "500px",
    marginLeft: "500px",
    marginTop: "50px",
    alignContent: "center"
}


export default class GraphForm extends React.Component {
    state = {
        fileName: "",
        predFrequency: "",
        numPred: "",
        graphType: ""
    }

    change= (e) => {
        this.setState({
            [e.target.name]: e.target.value
        });
    };

    onSubmit = e => {
        e.preventDefault();
        console.log(this.state)
    };


    render() {

        return(
            <div style={newblock}>
                <br/>
                <div style={{marginLeft: "20px", marginTop: "20px", alignContent: 'center', fontSize:'1.8em', fontWeight:'bold'}}> Generate a New Report</div>
                <br></br>
                <div style={{textAlign: 'left', alignContent:'center'}}>
                    <Form style={{marginLeft: '20px', marginRight: '20px'}}>
                        <label> Report Name: </label>
                        <br/>
                        <input
                            style={{width: "600px", height: "35px"}}
                            name="fileName"
                            placeholder="My New Report"
                            value = {this.state.fileName}
                            onChange = {e => this.change(e)}
                        />
                        <br/><br/>

                        <label> Graph Type: </label>
                        <br/>
                        <select
                            name = "graphType"
                            value = {this.state.graphType}
                            onChange={e => this.change(e)}
                        >
                            <option value = "Graph Type">Graph Type</option>
                            <option value = "Line">Line</option>
                            <option value = "Bar">Bar</option>
                            <option value = "Scatter">Scatter</option>
                            <option value = "Pie">Pie</option>
                        </select>
                        <br/>

                        <label> Prediction Frequency: </label>
                        <br/>
                        <select
                            name = "predFrequency"
                            value = {this.state.predFrequency}
                            onChange={e => this.change(e)}
                        >
                            <option value = "Prediction Frequency">Prediction Frequency</option>
                            <option value = "Daily">Daily</option>
                            <option value = "Weekly">Weekly</option>
                            <option value = "Monthly">Monthly</option>
                        </select>

                        <br/><br/>
                        <Button
                            variant="outline-dark"
                            type="submit"
                            style={{textAlign: 'center', marginLeft: '550px', width: "100px", height: "35px"}}
                            onClick = {(e) => this.onSubmit(e)}
                        >
                                Submit
                        </Button>

                    </Form>
                </div>
            </div>

        );
    }
}
