import React from "react";
import LineGraph from "../graphComponents/LineGraph";
import BarGraph from "../graphComponents/BarGraph";
import ScatterplotGraph from "../graphComponents/ScatterplotGraph";
import PieGraph from "../graphComponents/PieGraph";
//import PieGraph from "../graphComponents/PieGraph";

const newblock = {
    backgroundColor: "whitesmoke",
    borderRadius: "50px",
    display: "block",
    width: "600px",
    height: "350px",
    marginLeft: "650px",
    marginTop: "-349px",
    alignContent: "center"
}

export default class StaticGraph extends React.Component {
    constructor(props) {
        super(props);
        this.state = {

            legendPosition: this.props.legendPosition,
            graphType: this.props.graphType,

            chartData: {
                labels: this.props.labels,
                datasets:[{
                    label: this.props.dataLabel,
                    backgroundColor: this.props.colors,
                    fill: this.props.fill,
                    lineTension: 0,
                    pointRadius: 5,
                    data: this.props.data,
                }]
            },

            title: this.props.title,
            xAxisLabel: this.props.xAxisLabel,
            yAxisLabel: this.props.yAxisLabel,
            showLegend: this.props.showLegend
        }
    }

    render(){
        return(
            <div style = {newblock}>
                {this.state.graphType === "Bar" &&
                <BarGraph
                    chartData = {this.state.chartData}
                    title = {this.state.title}
                    xAxisLabel = {this.state.xAxisLabel}
                    yAxisLabel = {this.state.yAxisLabel}
                    showLegend = {false}
                />
                }
                {this.state.graphType === "Line" &&
                <LineGraph
                    chartData = {this.state.chartData}
                    title = {this.state.title}
                    xAxisLabel = {this.state.xAxisLabel}
                    yAxisLabel = {this.state.yAxisLabel}
                    showLegend = {this.state.showLegend}
                />
                }
                {this.state.graphType === "Scatter" &&
                <ScatterplotGraph
                    chartData = {this.state.chartData}
                    title = {this.state.title}
                    xAxisLabel = {this.state.xAxisLabel}
                    yAxisLabel = {this.state.yAxisLabel}
                    showLegend = {false}
                />
                }
                {this.state.graphType === "Pie" &&
                <PieGraph
                    chartData = {this.state.chartData}
                    title = {this.state.title}
                    showLegend = {this.state.showLegend}
                />}
            </div>)
    }
}
