import React from 'react'
import {
    BrowserRouter as Router,
    Route,
    Link, Switch,
} from 'react-router-dom'
import styled from "styled-components";
import {Home} from  '../pages/Home'
import {Nav, Navbar} from "react-bootstrap";
import Logo from "../static/Top_Logo.png";
import {Help} from "../pages/Help";
import {Settings} from "../pages/Settings";
import User from "../pages/User";
import {Reports} from "../pages/Reports";
import {NewReport} from "../pages/NewReport";
import {QBData} from "../pages/QBData";

const StyledSideNav = styled.div`
  position: fixed;   
  margin-top: 60px;  
  height: 100%;
  width: 130px;     
  z-index: 1;      
  top: 0px;      
  background-color: #deeff5; 
  overflow-x: hidden;    
  padding-top: 20px;
  text-color: white;
  align-content: center;
  align-text: center;
`;

const routes = [
    { path: '/connected',
        sidebar: () => <div>home!</div>,
        main: () =>  <Home/>
    },
    { path: '/reports',
        sidebar: () => <div>Reports!</div>,
        main: () => <Reports/>
    },
    { path: '/generate',
        sidebar: () => <div>New Graph!</div>,
        main: () => <NewReport/>
    },
    { path: '/qbdata',
        sidebar: () => <div>QBData!</div>,
        main: () => <QBData/>
    },
    { path: '/logout',
        sidebar: () => <div>Logout!</div>,
        main: () => <div></div>
    }
]

class SideNav extends React.Component {
    render() {
        return (
            <Router>
                <Navbar className="justify-content-end" bg="dark" variant="dark" fontFamily="Helvetica Neue,HelveticaNeue,Helvetica,Arial,sans-serif">
                    <div style={{color: 'white', alignContent: 'center', fontFamily: 'sans-serif', marginRight: '400px', fontSize: '1.4em', fontWeight: 'bolded'}}>
                        Sales Forecasting <img src={Logo} alt="Main Logo" hspace="5"/>
                    </div>
                    <Nav className="justify-content-right" fontFamily="Helvetica Neue,HelveticaNeue,Helvetica,Arial,sans-serif">
                        <li fontFamily="Helvetica Neue,HelveticaNeue,Helvetica,Arial,sans-serif"><Link to = {'/help'} className="nav-link">Help</Link></li>
                        <li fontFamily="Helvetica Neue,HelveticaNeue,Helvetica,Arial,sans-serif"><Link to = {'/settings'} className="nav-link">Settings</Link></li>
                        <li fontFamily="Helvetica Neue,HelveticaNeue,Helvetica,Arial,sans-serif"><Link to = {'/user'} className="nav-link">User</Link></li>
                    </Nav>
                </Navbar>
                <Switch>
                    <Route path='/help' component={Help}/>
                    <Route path='/settings' component={Settings}/>
                    <Route path='/user' component={User}/>
                </Switch>

                <div style={{ display: 'flex' }}>
                    <StyledSideNav>
                        <ul style={{ listStyleType: 'none', padding: 0 }}>
                            <li style={{marginLeft:'15px', color:'black'}}><Link to="/connected"> <i className="fa fa-home"></i> Home </Link></li>
                            <li style={{marginLeft:'15px', color:'black'}}><Link to="/reports"><i className="fa fa-folder-o"></i> Reports</Link></li>
                            <li style={{marginLeft:'15px', color:'black'}}><Link to="/generate"><i className="fa fa-line-chart"></i> Generate</Link></li>
                            <li style={{marginLeft:'15px', color:'black'}}><Link to="/qbdata"> <i className="fa fa-file-text"></i> QBData</Link></li>
                            <li style={{marginLeft:'15px', color:'black'}}><Link to="/logout"> <i className="fa fa-sign-out"></i> Logout</Link></li>
                        </ul>
                        {routes.map((route) => (
                            <Route
                                key={route.path}
                                path={route.path}
                                exact={route.exact}
                                component={route.sidebar}
                            />
                        ))}
                    </StyledSideNav>

                    <div style={{ flex: 1, padding: '10px' }}>
                        {routes.map((route) => (
                            <Route
                                key={route.path}
                                path={route.path}
                                exact={route.exact}
                                component={route.main}
                            />
                        ))}
                    </div>
                </div>
            </Router>
        )
    }
}

export default SideNav
