import React, {useState} from 'react';
import {ToggleButton, ToggleButtonGroup} from "react-bootstrap";


const newblock = {
    backgroundColor: "whitesmoke",
    borderRadius: "50px",
    display: "block",
    width: "1200px",
    height: "600px",
    marginLeft: "220px",
    marginTop: "40px"
}

function ToggleButtonGroupControlled() {
    const [value, setValue] = useState([1, 3]);
    const handleChange = (val) => setValue(val);
    return (
        <ToggleButtonGroup type="checkbox" value={value} onChange={handleChange}>
            <ToggleButton variant="dark" value={1}>Option 1</ToggleButton>
            <ToggleButton variant="dark" value={2}>Option 2</ToggleButton>
            <ToggleButton variant="dark" value={3}>Option 3</ToggleButton>
        </ToggleButtonGroup>
    );
}

export const Settings = (props) => (
    <>
        <div style={newblock}>
            <div style={{alignContent: 'center', marginLeft: '500px', marginTop: '20px', fontSize:'1.8em', fontWeight:'bold'}}> User Settings</div>
            <ToggleButtonGroupControlled />
        </div>
    </>
)
