import React from 'react';
import {Form, Button} from "react-bootstrap";
import {Link, Route} from "react-router-dom";
import Sales from "./Sales";
import LineGraph from "./graphComponents/LineGraph";
import BarGraph from "./graphComponents/BarGraph";
import scatterplotGraph from "./graphComponents/scatterplotGraph";
import PieGraph from "./graphComponents/PieGraph";


const newblock = {
    backgroundColor: "whitesmoke",
    borderRadius: "50px",
    display: "block",
    width: "750px",
    height: "600px",
    marginLeft: "50px",
    marginTop: "30px",
    alignContent: "center"
}

const newblock2 = {
    backgroundColor: "yellow",
    borderRadius: "50px",
    display: "block",
    width: "750px",
    height: "600px",
    marginLeft: "50px",
    marginTop: "500px",
    alignContent: "center"
}

const dataRoute = [
    { path: '/lineDisplay',
        main: () =>  <LineGraph name = "hello line"/>
    },
    {
        path: '/barDisplay',
        main: () => <BarGraph name = "hello bar"/>
    },
    {
        path: '/pieDisplay',
        main: () => <PieGraph name = "hello pie"/>
    },
    {
        path: '/scatterDisplay',
        main: () => <scatterplotGraph name = "hello scatter"/>
    }
]

export default class GraphForm extends React.Component {
    constructor(props) {
        super(props);
        this.setState = {
            fileName: '',
            predFrequency: '',
            numPred: 0,
            graphType: '',
            isSubmitted: false
        };

        this.onSubmit = this.onSubmit.bind(this);
    }

    change= (e) => {
        this.setState({
            [e.target.name]: e.target.value
        });
    };


    onSubmit = e => {
        this.setState({isSubmitted: true})
        e.preventDefault();
    };

    render() {
        return(<>
                 isSubmitted = {this.state};


                    <div>
                        <form onSubmit={this.onSubmit.bind(this)}>
                        </form>
                        {this.state.isSubmitted && <div style={newblock2}>hello</div>}
                     </div>

            </>
        );
    }
}
