/*
    Component to retrieve the Sales from the QuickBooks API
    Prints the most recent 25 sales from the sandbox
    @Notes: Lyka Marcelino, 04/04/2020
 */

import React from 'react';
import {Dropdown, Form, Button} from "react-bootstrap";
import InputGroup from "react-bootstrap/InputGroup";
const newblock = {
    backgroundColor: "yellow",
    borderRadius: "50px",
    display: "block",
    width: "700px",
    height: "500px",
    marginLeft: "500px",
    marginTop: "50px",
    alignContent: "center"
}


export default class GraphForm extends React.Component {
    state = {
        fileName: "",
        predFrequency: "",
        numPredictions: "",
    }

    change(event){
        this.setState({value: event.target.value})
    }

    submit(event){
        event.preventDefault();
        window.location.href =  "/displayGraph"
    }

    render() {
        return(
            <div style = {newblock}>
                <br/>
            <form style = {{marginLeft: '20px', marginTop: '20px'}}>
                <label style = {{fontSize: '1.8em', fontWeight: 'bold'}}>
                    Graph Name:
                    <br/>
                    <input
                        style={{fontSize: '1em'}}
                        type = "text"
                        placeholder= "My New graph"
                        value = {this.state.value}
                        onChange = {this.change}
                    />
                </label>
            </form>
            </div>
        );
    }
}
