/*
    Component to retrieve the Sales from the QuickBooks API
    Prints the most recent 25 sales from the sandbox
    @Notes: Lyka Marcelino, 04/04/2020
 */

import React from 'react';
import {Form, Button, Dropdown} from "react-bootstrap";

const newblock = {
    backgroundColor: "yellow",
    borderRadius: "50px",
    display: "block",
    width: "700px",
    height: "500px",
    marginLeft: "500px",
    marginTop: "50px",
    alignContent: "center"
}


export default class GraphForm extends React.Component {
    state = {
        fileName: "",
        predFrequency: "",
        numPred: ""
    }

    change= (e) => {
        this.setState({
            [e.target.name]: e.target.value
        });
    };

    onSubmit = e => {
        e.preventDefault();
        console.log(this.state)
    };

    onDropSubmit = e => {
        e.preventDefault();
        this.setState({
            [e.target.name]: e.target.value
        });
    };

    render() {

        return(
            <div style={newblock}>
                <br/>
                <div style={{marginLeft: "20px", marginTop: "20px", alignContent: 'center', fontSize:'1.8em', fontWeight:'bold'}}> Generate a New Report</div>
                <br></br>
                <div style={{textAlign: 'left', alignContent:'center'}}>
                    <Form style={{marginLeft: '20px', marginRight: '20px'}}>
                        <label> Report Name: </label>
                        <br/>
                        <input
                            style={{width: "650px", height: "35px"}}
                            name="fileName"
                            placeholder="My New Report"
                            value = {this.state.fileName}
                            onChange = {e => this.change(e)}
                        />
                        <br/><br/>

                        <Dropdown style={{marginTop: '10px'}}>
                            <Dropdown.Toggle variant="dark" id="dropdown-basic">Type of Graph</Dropdown.Toggle>
                            <Dropdown.Menu>
                                <Dropdown.Item href="#/action-1">Bar</Dropdown.Item>
                                <Dropdown.Item href="#/action-2">Line</Dropdown.Item>
                                <Dropdown.Item href="#/action-3">Pie</Dropdown.Item>
                                <Dropdown.Item href="#/action-3">Scatter plot</Dropdown.Item>
                            </Dropdown.Menu>
                        </Dropdown>

                        <br/>

                        <Dropdown
                            name = "predFrequency"
                            style = {{marginTop: '10px'}}
                            value = {this.state.predFrequency}
                            onChange = {(e) => this.onDropSubmit(e)}>
                            <Dropdown.Toggle variant="dark" id="dropdown-basic">Prediction Frequency</Dropdown.Toggle>
                            <Dropdown.Menu>
                                <Dropdown.Item href="#/action-1">Daily</Dropdown.Item>
                                <Dropdown.Item href="#/action-2">Weekly</Dropdown.Item>
                                <Dropdown.Item href="#/action-3">Monthly</Dropdown.Item>
                            </Dropdown.Menu>
                        </Dropdown>
                        <br/><br/><br/>

                        <Button
                            variant="outline-dark"
                            type="submit"
                            style={{textAlign: 'center', marginLeft: '550px', width: "100px", height: "35px"}}
                            onClick = {(e) => this.onSubmit(e)}
                        >
                                Submit
                        </Button>

                    </Form>
                </div>
            </div>

        );
    }
}
