import React, {Component} from 'react';
import {Line} from 'react-chartjs-2';
import {Button} from "react-bootstrap";

export default class ScatterplotGraph extends Component{
    constructor(props){
        super(props);
        this.state = {
            chartData:props.chartData
        }
    }

    static defaultProps = {
        displayTitle:true,
        displayLegend: true,
        legendPosition:'right',
        graphTitle: "Graph Title"
    }

    render(){
        return (
            <>
            <div className="scatterplotGraph">
                <Line
                    data={this.state.chartData}
                    options={{
                        title:{
                            display:this.props.displayTitle,
                            text:this.props.graphTitle,
                            fontSize:25
                        },
                        legend:{
                            display:this.props.displayLegend,
                            position:this.props.legendPosition
                        },
                        scales:{
                            yAxes:[{
                                scaleLabel:{
                                    display: true,
                                    labelString: 'Total Predicted Sales (USD)',
                                    fontSize: 20
                                }
                            }],
                            xAxes:[{
                                scaleLabel: {
                                    display: true,
                                    labelString: "Date",
                                    fontSize: 20
                                }
                            }]
                        }
                    }}
                />
            </div>
            {this.state.showButton &&
            <Button
                variant="outline-dark"
                type="submit"
                style={{textAlign: 'center', marginLeft: '250px', marginTop: "0px", width: "150px", height: "40px"}}
                onClick = {(e) => this.handleSubmit(e)}
            />}
           </>
        )
    }
}
