import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {TopNav} from "../components/TopNav";
import {Col, Container, Row} from "react-bootstrap";

const roundblock = {
    backgroundColor: "whitesmoke",
    borderRadius: "50px",
    display: "block",
    width: "5000px",
    height: "400px",
    marginLeft: "115px",
    marginTop: "24px",
}

const notifications ={
    backgroundColor: "whitesmoke",
    borderRadius: "50px",
    display: "block",
    width: "5010px",
    height: "200px",
    textAlign: "left",
    marginLeft: "115px",
    marginTop: "24px",
    alignContent: "left"
}

const urlParams = window.location;

function getParameterByName(name, url) {
    if (!url) url = window.location.href;
    name = name.replace(/[[\]]/g, '\\$&');
    var regex = new RegExp('[?&]' + name + '(=([^&#]*)|&|#|$)'),
        results = regex.exec(url);
    if (!results) return null;
    if (!results[2]) return '';
    return decodeURIComponent(results[2].replace(/\+/g, ' '));
}

var code = getParameterByName('code', urlParams);
var state = getParameterByName('state', urlParams);
var realmId = getParameterByName('realmId', urlParams);

function fetchData()
{
    fetch('http://localhost:8080/oauth', {method: 'GET', headers:{'code' : code, 'state': state, 'realmId': realmId}})
        .then(res => res.json())
        .catch(function (err) {
        return err;
        })
}

fetchData();

if(window.opener) {
    window.opener.location.href = '/connected'
    window.close()
}

export class Connected extends React.Component {
    render() {
        return (
            <>
                <Container>
                    <Row className="justify-content-md-center">
                        <Col md="auto" style = {roundblock}>
                            <div style={{alignContent: 'left', marginLeft: '10px', marginTop: '20px', fontSize:'1.8em', fontWeight:'bold'}}> User Dashboard</div>
                        </Col>
                    </Row>
                    <Row className="justify-content-md-center">
                        <Col md="auto" style={notifications}>
                            <div style={{textAlign:'left', marginLeft: '30px', marginTop: '20px', fontSize: '1.4em', fontWeight:'bold'}}> Notifications</div>
                            <div style={{marginTop:'5px'}}>
                                <i className="fa fa-exclamation-triangle" style={{marginLeft: '30px', marginRight: '9px'}}></i> Inventory warning there is not enough ____ to complete project sales on items.
                                <br></br>
                                <i className="fa fa-clock-o" style={{marginLeft: '30px', marginRight: '12px'}}></i>Current Inventory of item ____ will last until next week.
                                <br></br>
                                <i className="fa fa-lightbulb-o" style={{marginLeft: '30px', marginRight: '12px'}}></i> Sales for item _____ are projected to sell at a higher rate than expected!
                            </div>
                        </Col>
                    </Row>
                </Container>
            <TopNav/>
            </>
        );
    }
}
