import React from "react";
import BarGraph from "./graphComponents/BarGraph";

export default class GraphDisplay extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            graphName: this.props.graphName,
            numPred: this.props.numPred,
            graphType: this.props.graphType,
            predFrequency: this.props.predFrequency,
            showTable: this.props.showTable,
            from: this.props.from,
            labels: null,
            dataLabel: null,
            data: null,
            colors: null,
            legendPosition: null,
            title: null,
            xAxisLabel: null,
            yAxisLabel: null,
            showLegend: null,
        }
    }
    componentDidMount() {
        fetch('http://localhost:8080/getNewGraph', {
            method: 'GET',
            headers: {
                'graphName': this.state.graphName,
                'numPred': this.state.numPred,
                'graphType': this.state.graphType,
                'predFrequency': this.state.predFrequency,
                'from': this.state.from,
            }})
            .then(res => res.json())
            .then(jsonResponse => {
                this.setState({
                    labels: jsonResponse.labels,
                    dataLabel: jsonResponse.dataLabel,
                    data:jsonResponse.data,
                    colors: jsonResponse.colors,
                    legendPosition: jsonResponse.legendPosition,
                    title : jsonResponse.title,
                    xAxisLabel: jsonResponse.xAxisLabel,
                    yAxisLabel: jsonResponse.yAxisLabel,
                    showLegend: jsonResponse.showLegend
                })
            })
            .catch(function(error) {
                console.log('There has been a problem with your fetch operation: ' + error.message);
                throw error;
            })
    }


    render() {
        console.log(this.state)
        var      chartData = {
            labels: [this.state.labels[0], this.state.labels[1], "other label"],
            datasets:[
                {
                    label:'Population',
                    data:[
                        617594,
                        181045,
                        153060,
                        106519,
                        105162,
                        95072
                    ],
                    backgroundColor:[
                        'rgba(255, 99, 132, 0.6)',
                        'rgba(54, 162, 235, 0.6)',
                        'rgba(255, 206, 86, 0.6)',
                        'rgba(75, 192, 192, 0.6)',
                        'rgba(153, 102, 255, 0.6)',
                        'rgba(255, 159, 64, 0.6)',
                        'rgba(255, 99, 132, 0.6)'
                    ]
                }
            ]
        }

        return(

            <div style={{textAlign: 'center', alignContent:'center', paddingTop: '130px'}}>
                <BarGraph
                    chartData = {chartData}
                    title = {this.state.title}
                    xAxisLabel = {this.state.xAxisLabel}
                    yAxisLabel = {this.state.yAxisLabel}
                    showLegend = {this.state.showLegend}
                />
                <br/>
                {this.state.legendPosition}
                <br/>
                {this.state.labels[0]}
                <br/>
                {this.state.labels[1]}
                <br/>
                   {this.state.colors[1]}
            </div>

        );
    }
}
