import React from 'react';
import {Table} from "react-bootstrap";

export default class Estimates extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            items: []
        };
    }
    componentDidMount() {
        fetch('http://localhost:8080/getEstimates', {method: 'GET'})
            .then(res => res.json())
            .then(items=>this.setState({items}))
            .catch(function(error) {
                console.log('There has been a problem with your fetch operation: ' + error.message);
                throw error;
            })
    }
    render() {
        const {items} = this.state;
        let itemArray = new Array(items.length);
        let i = 0;
        for(var key in items){
            itemArray[i] = items[key];
            i = i + 1;
        }

        const names = [];
        const des = [];
        const doc =[];
        const txn = [];
        const qty = [];
        const price = [];

        function setNames() {
            for(var x in itemArray[0]){
                var temp = itemArray[0][x];
                names[x] = temp;
            }
        }

        function setDesc() {
            for(var x in itemArray[1]){
                var temp = itemArray[1][x];
                des[x] = temp;
            }
        }

        function setDoc() {
            for(var x in itemArray[2]){
                var temp = itemArray[2][x];
                doc[x] = temp;
            }
        }

        function setTxn() {
            for(var x in itemArray[3]){
                var temp = itemArray[3][x];
                txn[x] = temp;
            }
        }

        function setQuantity() {
            for(var x in itemArray[4]){
                var temp = itemArray[4][x];
                qty[x] = temp;
            }
        }

        function setPrice() {
            for(var x in itemArray[5]){
                var temp = itemArray[5][x];
                price[x] = temp;
            }
        }

        setNames();
        setDesc();
        setDoc();
        setTxn();
        setQuantity();
        setPrice();

        return (
            <>
                <div style={{marginLeft: '170px',marginTop: '0px',fontSize: '2rem', fontWeight: 'bold'}}>View your QuickBooks Data</div>
                <div style={{marginTop: '20px', marginLeft: '265px', width: '1100px', height: '400px'}}>
                    <Table striped bordered hover variant={true} size="sm">
                        <thead>
                        <tr>
                            <th>Invoice Name</th>
                            <th>Description</th>
                            <th>Document Number</th>
                            <th>Transaction Date</th>
                            <th>Quantity</th>
                            <th>Unit Price</th>
                        </tr>
                        </thead>
                        <tbody>
                        <tr>
                            <td>{names[0]}</td>
                            <td>{des[0]}</td>
                            <td>{doc[0]}</td>
                            <td>{txn[0]}</td>
                            <td>{qty[0]}</td>
                            <td>{price[0]}</td>
                        </tr>
                        </tbody>
                    </Table>
                </div>

            </>

        );

    }
}
